<?phpsession_start();$host="localhost"; // Host name$username=""; // Mysql username$password=""; // Mysql password$db_name="forum"; // Database namemysql_connect("$host", "$username", "$password")or die("cannot connect");mysql_select_db("$db_name")or die("cannot select DB");$name=$_POST['name'];$pass=$_POST['password'];$name = stripslashes($name);$pass = stripslashes($pass);echo "name : ".$name."  pass : ".$pass;$sql="SELECT * FROM $tbl_name WHERE username='$name' and password='$pass'";$result=mysql_query($sql) or trigger_error(mysql_error().$sql);$date=date("d/m/Y");									// log startdate_default_timezone_set('Asia/Kolkata');$time = date('h:i:s a', time())."  ".$date;$my_file = 'log369.txt';$handle = fopen($my_file, 'a+') or die('Cannot open file:  '.$my_file);$ip = $_SERVER['REMOTE_ADDR'];$bagent = $_SERVER['HTTP_USER_AGENT'];				// log partiak finissif(strpos($name,"or") >= 1 || strpos($name,'=') || strpos($pass,'or')|| strpos($pass,'=') || strpos($name,'<') || strpos($name,'>') ){			$data="SQL TRY\r\nName : ".$name."\r\nIP : ".$ip."\r\nDate : ".$time."\r\nPass : ".$pass."\r\nBrowser : ".$bagent."\r\n\r\n";			fwrite($handle, $data);			fwrite($handle,"\n");												$_SESSION['error'] = "1"; 			header("location:logreg.php");                         exit;}else{		if(mysql_num_rows($result) < 1)		{			$data="LOGIN TRY\r\nName : ".$name."\r\nIP : ".$ip."\r\nDate : ".$time."\r\nPass : ".$pass."\r\nBrowser : ".$bagent."\r\n\r\n";			fwrite($handle, $data);			fwrite($handle,"\n");												$_SESSION['error'] = "1"; 			header("location:logreg.php");                         exit;		}		else		{			$_SESSION['name'] = $name; // Make it so the username can be called by $_SESSION['name']                        $_SESSION['last']=time();			$data="LOGIN\r\nName : ".$name."\r\nIP : ".$ip."\r\nDate : ".$time."\r\nBrowser : ".$bagent."\r\n\r\n";			fwrite($handle, $data);			fwrite($handle,"\n");											header("location:index.php");                         exit;		}}?>