ass','$mail')";				$result=mysql_query($sql);				$_SESSION['name'] = $name;                                $_SESSION['last']=time();				$data="REGISTER\r\nName : ".$name."\r\nMail :  ".$mail."\r\nIP : ".$ip."\r\nDate : ".$time."\r\nBrowser : ".$bagent."\r\n\r\n";				fwrite($handle, $data);				fwrite($handle,"\n");				header("location:index.php");                                exit;		}	}}?>